import torch
from transformers import BertForSequenceClassification, BertTokenizer

class SensitiveContentClassifier:
    def __init__(self, model_path='best_model.pth', model_name='bert-base-multilingual-cased', max_length=128):
        """
        初始化分类器
        :param model_path: 训练好的模型权重路径（.pth 文件）
        :param model_name: 预训练模型名称
        :param max_length: 输入文本的最大长度
        """
        self.model_name = model_name
        self.max_length = max_length
        self.device = 'cuda' if torch.cuda.is_available() else 'cpu'  # 自动选择设备

        # 加载分词器和模型
        self.tokenizer = BertTokenizer.from_pretrained(model_name)
        self.model = BertForSequenceClassification.from_pretrained(model_name, num_labels=2)
        self.model.load_state_dict(torch.load(model_path, map_location=self.device))  # 加载模型权重
        self.model.to(self.device)
        self.model.eval()  # 设置为评估模式

    def preprocess_text(self, text):
        """
        预处理输入文本
        :param text: 输入文本
        :return: 包含 input_ids 和 attention_mask 的字典
        """
        encoding = self.tokenizer.encode_plus(
            text,
            add_special_tokens=True,
            max_length=self.max_length,
            truncation=True,
            padding='max_length',
            return_attention_mask=True,
            return_tensors='pt'
        )
        return encoding

    def predict(self, text):
        """
        对输入文本进行分类
        :param text: 输入文本
        :return: 分类结果（0: 非敏感, 1: 敏感）
        """
        # 预处理文本
        encoding = self.preprocess_text(text)
        input_ids = encoding['input_ids'].to(self.device)
        attention_mask = encoding['attention_mask'].to(self.device)

        # 进行预测
        with torch.no_grad():
            outputs = self.model(input_ids=input_ids, attention_mask=attention_mask)
            logits = outputs.logits
            preds = torch.argmax(logits, dim=1).cpu().numpy()

        return preds[0]  # 返回预测结果

    def classify(self, text):
        """
        对输入文本进行分类并返回结果
        :param text: 输入文本
        :return: 分类结果（字符串：'敏感' 或 '非敏感'）
        """
        prediction = self.predict(text)
        return "敏感" if prediction == 1 else "非敏感"

